class PageData {
  final String bgImage;
  final IconData icon;

  PageData({required this.bgImage, required this.icon});
}


class AutoPageView extends StatefulWidget {
  final List<PageData> pages;

  AutoPageView({required this.pages});

  @override
  _AutoPageViewState createState() => _AutoPageViewState();
}

class _AutoPageViewState extends State<AutoPageView> {
  final PageController _pageController = PageController();
  int _currentPage = 0;

  @override
  void initState() {
    super.initState();
    // Start the timer to auto-advance pages every 5 seconds
    Timer.periodic(Duration(seconds: 4), (timer) {
      if (_currentPage < widget.pages.length - 1) {
        _currentPage++;
      } else {
        Get.to(OptionPage());
      }
      _pageController.animateToPage(
        _currentPage,
        duration: Duration(milliseconds: 500),
        curve: Curves.easeInOut,
      );
    });
  }

  @override
  Widget build(BuildContext context) {
    return PageView.builder(
      controller: _pageController,
      itemCount: widget.pages.length,
      itemBuilder: (context, index) {
        return PageScreen(
          bgImage: widget.pages[index].bgImage,
          icon: widget.pages[index].icon,
          pageIndex: index,
          pageCount: widget.pages.length,
        );
      },
      onPageChanged: (index) {
        setState(() {
          _currentPage = index;
        });
      },
    );
  }

  @override
  void dispose() {
    _pageController.dispose();
    super.dispose();
  }
}

